import { Box, Flex } from "@chakra-ui/react";

const FullHeightOutlineBox = () => {
  return (
    <Flex
      direction="column" // 子要素を縦方向に配置
      h="100vh" // 画面領域いっぱいに縦方向に伸ばす
      w="100%" // 横方向にも画面領域いっぱいに伸ばす
      border="2px solid red" // ボーダーのスタイルを設定
      bg={"red"}
      // または outline="2px solid black"  // アウトラインのスタイルを設定
    >
      {/* 子要素にflex: 1を設定して親要素に合わせて伸ばす */}
      <div style={{ flex: 1 }}>{/* ここにコンテンツを追加する */}</div>
    </Flex>
  );
};

export default FullHeightOutlineBox;
