import { Box } from "@chakra-ui/react";

const FullHeightOutlineBox = () => {
  return (
    <Box
      h="100vh" // 画面領域いっぱいに縦方向に伸ばす
      w="100%" // 横方向にも画面領域いっぱいに伸ばす
      border="2px solid black" // ボーダーのスタイルを設定
      // または outline="2px solid black"  // アウトラインのスタイルを設定
    >
      {/* ここにコンテンツを追加する */}
    </Box>
  );
};

export default FullHeightOutlineBox;
